import React from 'react'
import styled from '@emotion/styled';
import { Link } from 'react-router-dom';

const MenuContainer = () => {

    const Options = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width:100%;
    height:92.7vh;
    background-color:lightcyan;
    `;

    const Item = styled.div`
    margin:10px;
    font-size: 40px;
    :hover{
        color: blue;
        cursor: pointer;
    }
    `;

    return ( 
        <Options>
            <Link to="/">
                <Item>Home</Item>                
            </Link>
            <Link to="/sticks">
                <Item>Sticks</Item>                
            </Link>
            <Link to="/sticks">
                <Item>Skates</Item>
            </Link>
            <Link to="/sticks">
                <Item>Helmets</Item>                
            </Link>
            <Link to="/sticks">
                <Item>Undergear</Item>                
            </Link>
            <Link to="/sticks">
                <Item>Padding</Item>                
            </Link>
        </Options> 
     );
}
 
export default MenuContainer
