import React from 'react'
import styled from '@emotion/styled';


const MenuContainer = () => {

let bg = "none";

    const Options = styled.div`
    display: ${bg};
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width:100%;
    height:93vh;
    background-color:lightcyan;
    `;

    const Item = styled.div`
    margin:10px;
    font-size: 40px;
    :hover{
        color: blue;
        cursor: pointer;
    }
    `;

    return ( 
        <Options>
            <Item>Skates</Item>
            <Item>Sticks</Item>
            <Item>Helmets</Item>
            <Item>Padding</Item>
            <Item>Helmets</Item>
            <Item>Undergear</Item>
        </Options> 
     );
}
 
export default MenuContainer
