import React from 'react'
import styled from '@emotion/styled';
import { Link } from 'react-router-dom';

const MenuContainer = (props) => {

let bg = "";

if(props.navBarOpen){
    bg="flex";
} else {
    bg="none";
}

    const Options = styled.div`
    display: ${bg};
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width:100%;
    height:93vh;
    background-color:lightcyan;
    `;

    const Item = styled.div`
    margin:10px;
    font-size: 40px;
    :hover{
        color: blue;
        cursor: pointer;
    }
    `;

    function changeBg(){
        bg="none"
    }

    return ( 
        <Options>
            <Link to="/sticks" onClick={changeBg}>
                <Item>Sticks</Item>                
            </Link>
            <Item>Skates</Item>
            <Item>Helmets</Item>
            <Item>Padding</Item>
            <Item>Helmets</Item>
            <Item>Undergear</Item>
        </Options> 
     );
}
 
export default MenuContainer
