import React from 'react'
import styled from '@emotion/styled';
import { Link } from 'react-router-dom';

const MenuContainer = () => {

    const Options = styled.div`
    display: flex;
    flex-direction: row;
    flex-wrap:wrap;
    align-items: center;
    justify-content: center;
    height:100%;
    background: none;
    width: 80%;
    `;

    const Item = styled.div`
    margin:10px;
    font-size: 20px;
    color:white;
    @media(max-width: 500px){
        margin:5px;
        font-size:15px;
    }
    :hover{
        color: blue;
        cursor: pointer;
    }
    `;

    return ( 
        <Options>
            <Link to="/sticks" style={{textDecoration:'none'}}>
                <Item>Sticks</Item>                
            </Link>
            <Link to="/skates" style={{textDecoration:'none'}}>
                <Item>Skates</Item>
            </Link>
            <Link to="/helmets" style={{textDecoration:'none'}}>
                <Item>Helmets</Item>                
            </Link>
            <Link to="/undergear" style={{textDecoration:'none'}}>
                <Item>Undergear</Item>                
            </Link>
            <Link to="/padding" style={{textDecoration:'none'}}>
                <Item>Padding</Item>                
            </Link>
        </Options> 
     );
}
 
export default MenuContainer
