import React,{useState} from 'react';
import {addCart, romoveCart} from '../redux/index';
import {connect} from 'react-redux'

const CartContainer=(props)=>{

const[itemObj, setItemObj]=useState({
    numOfitem: 1,
    item: "bauer"
});

return(
    <div>
        <h2>Number in cart - {props.numItems}</h2>
        <input type = "text" value={itemObj} onChange={e=>setItemObj({numOfItem:e.target.value, item: "bauer"})}/>
        <button onClick={()=>props.addCart(itemObj)}>Add Cart</button>
    </div>
)

}


const mapStateToProps=state=>{
    return {
        numItems: state.cart.numItems
    }
}
 
const mapDispatchToProps = dispatch =>{
    return{
        addCart: itemObj=>dispatch(addCart(itemObj))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CartContainer)
