import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import {connect} from 'react-redux'
import MenuContainer from './menuOptions';
import './navBar.css'


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));

function ButtonAppBar(props) {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <AppBar>
        <Toolbar  className="navBar">
          <Typography className="nameLogo">
            Tak's Hockey<img className="logo" src={require('./ice-hockey.png')} alt="icehockey" />
          </Typography>

          <MenuContainer/> {/*Menu*/}

          <div style={{display:"flex", flexDirection:"column", alignItems:'center'}}>
            <div>{props.numItems.length}</div>
            <img className="cartLogo" src={require('./cart.png')} alt="icehockey" />
          </div>
        </Toolbar>
      </AppBar>
    </div>
  );
}

const mapStateToProps=state=>{
  return {
      numItems: state.cart
  }
}

export default connect(mapStateToProps, null)(ButtonAppBar)
