import React,{useState} from 'react';
import {addCart, romoveCart} from '../redux/index';
import {connect} from 'react-redux'

const CartContainer=(props)=>{

const[numCart, setNumCart]=useState(1);

return(
    <div>
        <h2>Number in cart - {props.numItems}</h2>
        <input type = "text" value={numCart} onChange={e=>setNumCart(e.target.value)}/>
        <button onClick={()=>props.addCart(numCart)}>Add Cart</button>
    </div>
)

}


const mapStateToProps=state=>{
    return {
        numItems: state.cart.numItems
    }
}
 
const mapDispatchToProps = dispatch =>{
    return{
        addCart: numItems=>dispatch(addCart(numItems))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CartContainer)
