import React from 'react';
import {addCart} from '../redux/index';
import {removeCart} from '../redux/index';
import {connect} from 'react-redux'
import { v4 as uuidv4 } from 'uuid';


const CartContainer=(props)=>{
const Id = uuidv4();
return(
    <div>
        <button onClick={()=>props.addCart({item: "bauer", itemId:{Id}})}>Add Cart</button>
        <button onClick={()=>props.removeCart({Id})}>Remove Cart</button>
    </div>
)

}


const mapStateToProps=state=>{
    return {
        numItems: state.cart
    }
}
 
const mapDispatchToProps = dispatch =>{
    return{
        addCart: itemObj=>dispatch(addCart(itemObj)),
        removeCart: itemId=>dispatch(removeCart(itemId))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CartContainer)
