import * as actions from './cartActionType'

export const removeCart=(itemId)=>{
    return {
            type: actions.REMOVE_CART,
            payload: itemId
        }
    }
export const addCart=(itemObj)=>{
return {
        type: actions.ADD_CART,
        payload: itemObj
    }
}
