import React from 'react'
import styled from '@emotion/styled';
import { Link } from 'react-router-dom';

const MenuContainer = () => {

    const Options = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    height:100%;
    background: none;
    width: 80%;
    `;

    const Item = styled.div`
    margin:10px;
    font-size: 20px;
    color:white;
    text-decoration:none;
    :hover{
        color: blue;
        cursor: pointer;
    }
    `;

    return ( 
        <Options>
            <Link to="/">
                <Item>Home</Item>                
            </Link>
            <Link to="/sticks">
                <Item>Sticks</Item>                
            </Link>
            <Link to="/skates">
                <Item>Skates</Item>
            </Link>
            <Link to="/helmets">
                <Item>Helmets</Item>                
            </Link>
            <Link to="/undergear">
                <Item>Undergear</Item>                
            </Link>
            <Link to="/padding">
                <Item>Padding</Item>                
            </Link>
        </Options> 
     );
}
 
export default MenuContainer
