import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import {connect} from 'react-redux'
import MenuContainer from './menuOptions.js'



const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));

function ButtonAppBar(props) {
  const classes = useStyles();

  const[navBarOpen, setNavBarOpen]=useState(false);

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
          <IconButton onClick={()=>setNavBarOpen(!navBarOpen)} edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
            <MenuIcon/>
          </IconButton>
          <Typography variant="h6" className={classes.title}>
            Tak's Hockey<img style={{width: "30px", marginLeft:"3px"}} src={require('./ice-hockey.png')} alt="icehockey" />
          </Typography>
  <Button color="inherit">Cart {props.numItems.length}</Button>
        </Toolbar>
      </AppBar>
      {/*open menu when navBarOpen is true*/}
      <MenuContainer navBarOpen={navBarOpen}/>
    </div>
  );
}

const mapStateToProps=state=>{
  return {
      numItems: state.cart
  }
}

export default connect(mapStateToProps, null)(ButtonAppBar)
