import React,{useState} from 'react';
import {addCart, romoveCart} from '../redux/index';
import {connect} from 'react-redux'
import { v4 as uuidv4 } from 'uuid';


const CartContainer=(props)=>{

return(
    <div>
        <h2>Number in cart - {props.numItems}</h2>
        <button onClick={()=>props.addCart({item: "bauer", itemId:uuidv4()})}>Add Cart</button>
    </div>
)

}


const mapStateToProps=state=>{
    return {
        numItems: state.cart.state
    }
}
 
const mapDispatchToProps = dispatch =>{
    return{
        addCart: itemObj=>dispatch(addCart(itemObj))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CartContainer)
