import React from 'react';
import styled from '@emotion/styled';
import {addCart} from '../../redux/index';
import {removeCart} from '../../redux/index';
import {connect} from 'react-redux'

const StickPage = styled.div`
display: flex;
flex-direction: column;
width:100%;
height:92.7vh;
`;
const StickTitle = styled.div`
height:10%;
font-size: 50px;
text-align: center;
`
const SticksDisplay = styled.div`
display: flex;
flex-direction: row;
flex-wrap: wrap;
width:100%;
height: 90%;
justify-content: center;
align-items:center;
`
const Item = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items:center;
margin:70px;
`
const SticksContainer = (props) => {
    return (  
        <StickPage>
            <StickTitle>Sticks</StickTitle>
            <SticksDisplay style={{display:"flex",flexDirection:"row", backgroundColor:"white"}}>
                <Item>
                    <img style={{width: "200px", marginLeft:"3px"}} src={require('./ice-hockey.png')} alt="icehockey" />
                    <div>Bauer Stick</div>
                    <div>$300</div>
                    <button onClick={()=>props.addCart({item: "Bauer", itemId:1})}>Add Cart</button>
                </Item>
                <Item>
                    <img style={{width: "200px", marginLeft:"3px"}} src={require('./ice-hockey.png')} alt="icehockey" />
                    <div>Warrior Stick</div>
                    <div>$300</div>
                    <button onClick={()=>props.addCart({item: "Warrior", itemId: 2})}>Add Cart</button>
                </Item>
                <Item>
                    <img style={{width: "200px", marginLeft:"3px"}} src={require('./ice-hockey.png')} alt="icehockey" />
                    <div>CCM Stick</div>
                    <div>$300</div>
                    <button onClick={()=>props.addCart({item: "CCM", itemId:3})}>Add Cart</button>
                </Item>
                <Item>
                    <img style={{width: "200px", marginLeft:"3px"}} src={require('./ice-hockey.png')} alt="icehockey" />
                    <div>SHERWOOD Stick</div>
                    <div>$300</div>
                    <button onClick={()=>props.addCart({item: "Sherwood", itemId:4})}>Add Cart</button>
                </Item>
            </SticksDisplay>
        </StickPage>
    );
}
 
const mapStateToProps=state=>{
    return {
        numItems: state.cart
    }
}
 
const mapDispatchToProps = dispatch =>{
    return{
        addCart: itemObj=>dispatch(addCart(itemObj)),
        removeCart: itemId=>dispatch(removeCart(itemId))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SticksContainer)
