import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import {connect} from 'react-redux'
import { Link } from 'react-router-dom';


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));

function ButtonAppBar(props) {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
        <Link style={{color:"white"}}to="/menu">
          <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
            <MenuIcon/>
          </IconButton>
        </Link>
          <Typography variant="h6" className={classes.title}>
            Tak's Hockey<img style={{width: "30px", marginLeft:"3px"}} src={require('./ice-hockey.png')} alt="icehockey" />
          </Typography>
        <div style={{display:"flex", flexDirection:"column", alignItems:'center'}}>
          <div>{props.numItems.length}</div>
          <img style={{width: "30px", color:"secondary"}} src={require('./cart.png')} alt="icehockey" />
        </div>
        </Toolbar>
      </AppBar>
    </div>
  );
}

const mapStateToProps=state=>{
  return {
      numItems: state.cart
  }
}

export default connect(mapStateToProps, null)(ButtonAppBar)
