import * as actions from './cartActionType'

const initialState={
    numItems: 0,
    items:[]
}

const cartReducer = (state=initialState, action)=>{
    switch(action.type){
        case actions.ADD_CART:
            return {
                numItems: state.numItems + action.payload.numOfItem,
                items: [...state.items, action.payload.item]
            }
        case actions.REMOVE_CART:
            return state.numItems!==0? {
                numItems: state.numItems - action.payload.numItems,
                items: [...state.items]
            } : state
        default:
            return state
    }
}

export default cartReducer
